#!/bin/sh
qemu-system-aarch64 -m 1024 -cpu cortex-a72 -M virt -nographic --no-reboot -kernel ../linux/arch/arm64/boot/Image -append "console=ttyAMA0 root=/dev/vda vm=vm1" -drive file=../alpine-rootfs/rootfs.img,format=raw,snapshot=on -netdev user,id=netuser,net=192.168.100.0/24,hostfwd=tcp::2201-:22 -device e1000,netdev=netuser -netdev socket,id=netrouter1,connect=:53481 -device e1000,netdev=netrouter1 -object filter-dump,id=filterrouter1,netdev=netrouter1,file=/tmp/vm1-router1.pcap